import { useRouter } from 'next/router'
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import Layout from '../../../../components/MyLayout';
import Router from "next/router";
import redirect from 'next-redirect';
import Components from "../../../../components/components.js";
// import { Responsive, WidthProvider } from "react-grid-layout";

// url ===> http://localhost:3000/section/world/2020-01-11/iraq-iran-us-troops-4c50e545-539e-4893-b505-1edc2de3c977

const TextBox = ({ label }) => (
  <React.Fragment>
    <label>{label}</label>
  </React.Fragment>
);

const Template = ({ data: { gridLayout } }) => {
  return gridLayout.rows.map(row => {
    return (
      <div className="row" key={row.id}>
        {row.column.map(col => {
          return (
            <div className={`col-${col.width}`} key={col.id}>
              <Components block={col.block} />
            </div>
          );
        })}
      </div>
    );
  });
};

const Templatex = ({ data: { gridLayout } }) => {
  return <h1>ok</h1>
};

const News = (props) => {
  console.log('---------------------------------------------1');

  const grid = {
    "rows": [{
      "id": "r1",
      "column": [{
        "component": "foo",
        "width": "4",
        "id": "test1",
        "block": {
          _uid: "BUY6Drn9e1",
          component: "foo",
          text: "Foo"
        }
      }, {
        "component": "bar",
        "width": "4",
        "id": "test2",
        "block": {
          _uid: "BUY6Drn9e1",
          component: "bar",
          text: "Bar"
        }
      }]
    },
    {
      "id": "r2",
      "column": [{
        "component": "boo",
        "width": "6",
        "id": "test3",
        "block": {
          _uid: "BUY6Drn9ev",
          component: "boo",
          text: "Boo"
        }
      }, {
        "component": "bar",
        "width": "6",
        "id": "test4",
        "block": {
          _uid: "BUY6Drn9e1",
          component: "bar",
          headline: "Bar"
        }
      }]
    }
    ]
  }

  const data = {
    "gridLayout": {
      "rows": [{
        "id": "r1",
        "column": [{
          "component": "foo",
          "width": "4",
          "id": "test1",
          "block": {
            _uid: "BUY6Drn9e1",
            component: "foo",
            text: "Foo"
          }
        }, {
          "component": "bar",
          "width": "4",
          "id": "test2",
          "block": {
            _uid: "BUY6Drn9e1",
            component: "bar",
            text: "Bar"
          }
        }]
      },
      {
        "id": "r2",
        "column": [{
          "component": "boo",
          "width": "6",
          "id": "test3",
          "block": {
            _uid: "BUY6Drn9ev",
            component: "boo",
            text: "Boo"
          }
        }, {
          "component": "bar",
          "width": "6",
          "id": "test4",
          "block": {
            _uid: "BUY6Drn9e1",
            component: "bar",
            headline: "Bar"
          }
        }]
      }
      ]
    }
  }

  if (props.notFound) {
    const router = useRouter();
    router.replace('/notfound');
    return;
  }

  return (
    <Layout>
      <h1>News story</h1>
      <h1>uuid:: {props.uuid}</h1>
      <h1>title:: {props.data.title}</h1>

      <ul>
        <li>
          <Link href="/section/[section]/[date]/[id]" as="/section/world/2020-01-11/iraq-iran-us-troops-4c50e545-539e-4893-b505-1edc2de3c9ZZ">
            <a>Bad link</a>
          </Link>
        </li>
        <li>
          <Link href="/section/[section]/[date]/[id]" as="/section/world/2020-01-11/iraq-iran-us-troops-4c50e545-539e-4893-b505-1edc2de3c977">
            <a>Good link</a>
          </Link>
        </li>
        <li>
          <Link href="/section/[section]/[date]/[id]" as="/section/world/2020-01-11/iraq-iran-us-troops-4c50e545-539e-4893-b505-1edc2de3c988">
            <a>Good link</a>
          </Link>
        </li>
        <li>
          <Link href="/section/[section]/[date]/[id]" as="/section/world-middleeast/2020-01-11/iraq-iran-us-troops-4c50e545-539e-4893-b505-1edc2de3c999">
            <a>Good link</a>
          </Link>
        </li>
      </ul>

      <Template data={data} />

      <Templatex grid={grid} />


    </Layout>
  )
}

News.getInitialProps = async function (context) {

  const { section, date, id } = context.query;
  const uuid = id.slice(-36);
  const titleUrl = id.slice(0, id.length - 36);
  const url = '/section/' + section + '/' + date + '/' + id;
  const path = 'https://data.joornalo.com/news/' + uuid.charAt(0) + '/' + uuid.charAt(1) + '/' + uuid + '.json';

  console.log(uuid);

  try {
    const res = await fetch(path);
    const data = await res.json();

    if (url !== data['url']) {
      console.log(`Bad URL...redirected`);
      return redirect(context, data['url'], 308);
    }

    return {
      uuid,
      data
    };

  } catch (e) {
    //console.log(`Page not found`);
    return redirect(context, '/notfound', 302);
  }

};

export default News
