import React, { useReducer } from 'react';
import ConfigContext from './configContext';
import ConfigReducer from './configReducer';
import { GET_AGENT, REMOVE_ALERT } from '../types';

const ConfigState = props => {
  const initialState = {
    agent: ''
  };

  const [state, dispatch] = useReducer(ConfigReducer, initialState);

  // Set Alert
  const getAgent = (msg, type) => {

    let userAgent
    if (process.browser) {
      userAgent = navigator.userAgent
    } else {
      userAgent = context.req.headers['user-agent']
    }

    const mobilex = userAgent.match(/(Mobile)/g);
    const android = userAgent.match(/(Android)/g);
    const iPad = userAgent.match(/(iPad)/g);

    const mobile = Boolean(mobilex) && !iPad;
    const tablet = (!mobilex && Boolean(android)) || Boolean(iPad);
    const desktop = !mobile && !tablet;

    const agent = ((desktop || tablet) ? 'desktop' : 'mobile');
    console.log('agent----------------------');
    console.log(agent);

    dispatch({
      type: GET_AGENT,
      payload: { agent }
    });

  };

  return (
    <ConfigContext.Provider
      value={{
        config: state,
        getAgent
      }}
    >
      {props.children}
    </ConfigContext.Provider>
  );
};

export default ConfigState;
