import { useRouter } from 'next/router'
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import Layout from '../../../../components/MyLayout';
import redirect from 'next-redirect';

// url ===> http://localhost:3000/section/world-middleeast/2020-01-11/iraq-iran-us-troops-4c50e545-539e-4893-b505-1edc2de3c977


const News = (props) => {
  // const router = useRouter()
  // const { id, date, section } = router.query
  // const uuid = id.slice(-36);
  // const title = id.slice(0, id.length - 36);

  // const shows = props.shows;

  return (
    <Layout>
      <h1>News story</h1>
      <h1>uuid:: {props.uuid}</h1>
      <h1>title:: {props.titleUrl}</h1>
      <h1>section:: {props.section}</h1>
      <h1>date:: {props.date}</h1>

      {/* <ul>
        {props.shows.map(show => (
          <li key={show.id}>
            <Link href="/p/[id]" as={`/p/${show.id}`}>
              <a>{show.name}</a>
            </Link>
          </li>
        ))}
      </ul> */}

    </Layout>
  )
}

News.getInitialProps = async function (context) {
  const { section, date, id } = context.query;

  const uuid = id.slice(-36);
  const titleUrl = id.slice(0, id.length - 36);

  console.log('sectionId:', section);
  console.log('date:', date);
  console.log('uuid:', uuid);
  console.log('titleUrl:', titleUrl);

  const path = 'https://data.joornalo.com/' + uuid.charAt(0) + '/' + uuid.charAt(1) + '/' + uuid + '.json';

  const res = await fetch(path);
  const data = await res.json();

  console.log('----------------------');
  console.log(id);

  console.log(section);
  console.log(data['section-id']);

  console.log(date);
  console.log(data['date']);

  console.log(titleUrl);
  console.log(data['title-url'] + '-');

  // console.log(`Show data fetched: ${data}`);

  const url = '/section/' + section + '/' + date + '/' + id;
  console.log('=====================');
  console.log(url);

  if (url !== data['title-url'] + '-') {
    console.log(`Bad URL`);
    //return redirect(context, '/section/world/2020-01-11/iraq-iran-us-troops-4c50e545-539e-4893-b505-1edc2de3c977');
  }

  return {
    section,
    date,
    uuid,
    titleUrl,
    data
  };
  // return {};
};

export default News
