import React from "react";
import NewsBlock from "./newsBlock.js";

const Opinion1 = ({ data }) => {
  return (
    <div className="opinion1">
      <div className="blockheader"><span>Opinion</span></div>
      {data.opinion.map((news, index) => (
        <>
          <NewsBlock news={news} displayDesc={false} key={index}></NewsBlock>
        </>
      ))}
    </div>
  )
}

export default Opinion1;
