  console.log(id)

  useEffect((id) => {
    console.log('id::::', id)
    const contentWidth = document.getElementById(id).offsetWidth;
    console.log(contentWidth);
    // const myRef = React.createRef();
    // console.log(myRef.current.offsetWidth);
  });

  return (
    <div className='adv' id={id}>
      Advertising
    </div>
  )
}

