  //console.log(id)

  useEffect(() => {
    console.log('id::::', id)
    const div = document.getElementById(id);
    if (div) {
      const contentWidth = div.offsetWidth
      console.log(contentWidth)
    }

    //console.log(contentWidth);
    // const myRef = React.createRef();
    // console.log(myRef.current.offsetWidth);
  });

  return (
    <div className='adv' id={id}>
      Advertising
    </div>
  )
}

