import Components from "./components.js";

const Template = ({ grid, data }) => {
  if (!grid || !data) return (<div></div>);

  return grid.rows.map((row, index) => {
    return (
      <div className="row" key={`row-${index}`}>
        {row.column.map((col, indexz) => {
          if (col.components) {
            return (
              <div className={`${col.width}`} key={`col-${indexz}`}>
                {col.components.map((component, indexx) => {
                  return (
                    <Components component={component} data={data} text={col.text} />
                  )
                })}
              </div>
            );
          } else {
            if (col.rows) {
              return (
                <div className={`${col.width}`} key={`col-${index}`}>
                  <Template grid={col} data={data} />
                </div>
              );
            }
          }
        })}
      </div>
    );
  });
};

export default Template;
