import React from "react";
import Link from 'next/link'

const NewsBlock = ({ news }) => {

  return (
    <div className="NewsBlock">
      <Link href={news.url}>
        <img src={imageUrl(news.image)} />
        <h2>{news.title}</h2>
        <div>{news.desc}</div>
      </Link>
    </div>
  )
}

const imageUrl = (url) => {
  return 'https://data.joornalo.com' + url;
}

export default NewsBlock;
