import React, { useState, useEffect } from "react";

const Adv = (context) => {
  console.log('context---------------------------')
  console.log(context.params.size)
  const [msg, setMsg] = useState('---')
  const [id, setId] = useState(null)
  const [contentWidth, setContentWidth] = useState(0)

  let loaded = false;
  let isIntersecting = false;
  let banner = context.params.size;

  /*
  300x250 - Medium Rectangle - 40%
  728x90 - Leaderboard - 25%
  160x600 - Wide Skyscraper - 12%
  300x600 - Half Page - 5%
  970x250 - Billboard - 1%
  120x600 - Skyscraper

  320x50 - Mobile Leaderboard - 12%
  320x320 - Mobile Full Page Flex - 1%
  320x100 - Large Mobile
  */

  useEffect(() => {
    if (!id) {
      setId('adv' + Math.round(Math.random() * 100000))
    }

    const div = document.getElementById(id);
    if (div) {
      setContentWidth(div.offsetWidth)

      try {
        let observer = new IntersectionObserver((entries, observerChild) => {
          if (!loaded && entries[0].isIntersecting) {
            loaded = true;
            isIntersecting = true;
            observerChild.unobserve(entries[0].target);
            preLoad();
          }
        });
        observer.observe(div);
      } catch (err) {
        safariIssue();
      }
    }
  });

  const safariIssue = () => {
    if (!loaded) {
      loaded = true;
      isIntersecting = true;
      preLoad();
    }
  }

  /*
 300x250 - Medium Rectangle - 40%
 728x90 - Leaderboard - 25%
 160x600 - Wide Skyscraper - 12%
 300x600 - Half Page - 5%
 
 120x600 - Skyscraper

  970x250 - Billboard - 1%
 */

  const preLoad = () => {
    if (isIntersecting) {
      // if (contentWidth > 969) {
      //   banner = 'banner970x250.jpg'
      // }

      // if (contentWidth > 969) {
      //   banner = 'banner970x250.jpg'
      // }

      console.log('Ready to load...............................')
      setMsg('Loaded')
    }
  }

  return (
    <div className='adv' id={id}>
      Ad {banner} ({contentWidth}): {msg}
    </div>
  )
}

export default Adv;
