import React, { useRef, useEffect } from "react";

let msg = '---'

const Adv = (context) => {

  let loaded = false;
  let isIntersecting = false;
  const id = 'adv' + Math.round(Math.random() * 100000);


  useEffect(() => {
    const div = document.getElementById(id);
    if (div) {
      const contentWidth = div.offsetWidth
      console.log(id, '::::', contentWidth)

      try {
        let observer = new IntersectionObserver((entries, observerChild) => {
          if (!loaded && entries[0].isIntersecting) {
            loaded = true;
            isIntersecting = true;
            observerChild.unobserve(entries[0].target);
            preLoad();
          }
        });
        observer.observe(div);
      } catch (err) {
        safariIssue();
      }
    }
  });

  const safariIssue = () => {
    if (!loaded) {
      loaded = true;
      isIntersecting = true;
      preLoad();
    }
  }

  const preLoad = () => {
    if (isIntersecting) {
      console.log('Ready to load...............................')
      msg = 'Loaded'
    }
  }

  return (
    <div className='adv' id={id}>
      Advertising {msg}
    </div>
  )
}

export default Adv;
